class Motor:
    def __init__(self):
        self.name = 'Motor Car'
        self.brand = 'Brand Car'

    def motor_details(self):
        print(f"Motor details are {self.name} and brand is {self.brand}")

motor = Motor()
motor.motor_details()

class Motor:
    def __init__(self):
        self.name = 'Motor Car'
        self.brand = 'Brand Car'

    def motor_details(self):
        print(f"Motor details are {self.name} and brand is {self.brand}")

class BMW(Motor):
    def __init__(self):
        super().__init__()
        self.model = 'New Model'

    def bmw_motor_details(self):
        print(f"Motor details are {self.name} and brand is {self.brand} and model is {self.model}")

bmw = BMW()
bmw.bmw_motor_details()

class Motor:
    def __init__(self):
        self.name = 'Motor Car'
        self.brand = 'Brand Car'

    def motor_details(self):
        print(f"Motor details are {self.name} and brand is {self.brand}")

class Honda(Motor):
    def motor_details(self):
        print("Motor details for Honda car are the same")

honda = Honda()
honda.motor_details()

class Motor(ABC):
    @abstractmethod
    def motor_details(self):
        pass

class BMW(Motor):
    def motor_details(self):
        print("This is BMW car")

bmw = BMW()
bmw.motor_details()
